package by.epam.tr.main;

public class Lin14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double r;
		double s;
		double l;
		
		r = 3.4;
		s = Math.PI * r * r;
		l = Math.PI * 2 * r;
		
		System.out.println("Long = " + l);
		System.out.println("Square = " + s);

	}

}
