package by.epam.tr.main;

public class Lin15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi;
		double a;
		
		pi = Math.PI;
		System.out.println("pi^1 = " + pi);
		
		a = Math.pow(pi, 2);
		System.out.println("pi^2 = " + a);

		a = Math.pow(pi, 3);
		System.out.println("pi^3 = " + a);

		a = Math.pow(pi, 4);
		System.out.println("pi^4 = " + a);

	}

}
