package by.epam.tr.main;

public class Lin27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		int b;
		int c;
		int d;
		int e;

		a = 2;
		b = a * a;
		c = b * b;
		d = c * c;
		e = b * d;
		System.out.println("a^8 = " + d);
		System.out.println("a^10 = " + e);

	}

}
