public class Lin39 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x;
		double otvet;

		x = 5.5;
		
		otvet = ((((2 * x - 3) * x + 4) * x - 5) * x) + 6;
		System.out.print("((((2 * x - 3) * x +4)* x -5) * x +6 = " + otvet);

	}

}
