package by.epam.tr.main;

public class Lin40 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x;
		double otvet1;
		double otvet2;
		
		x = 1.0;
		
		otvet1 = ((4 * x + 3) * x + 2) * x + 1;
		otvet2 = ((-4 * x + 3) * x - 2) * x;
		System.out.println("((4 * x + 3) * x + 2) * x + 1 = " + otvet1);
		System.out.println("((-4 * x + 3) * x - 2) * x = " + otvet2);
	}

}
