public class Vetv08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a;
		double b;
		
		a = 2.0;
		b = -3.5;
		
		a = a * a;
		b = b * b;

		if (a < b) {
			System.out.println("least square = " + a);
		} else {
			System.out.println("least square = " + b);
		}
	}

}
